  app.post("/api/create-payment-intent", requireSupabaseAuth, async (req: AuthenticatedRequest, res) => {
    try {
      const { packageId } = req.body;

      if (!packageId) {
        return res.status(400).json({ message: "Package ID is required" });
      }

      // Get user's stripe mode preference
      const user = await storage.getUser(req.user.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Get correct Stripe instance based on user's mode preference
      const stripe = getStripeInstance(user.stripeMode || 'test');

      // Get package details from database (trusted source)
      const creditPackage = await storage.getCreditPackage(packageId);

      if (!creditPackage || !creditPackage.isActive) {
        return res.status(404).json({ message: "Credit package not found or inactive" });
      }

      // Use server-side price (prevent client tampering)
      const amount = parseFloat(creditPackage.price);

      const paymentIntent = await stripe.paymentIntents.create({
        amount: Math.round(amount * 100), // Convert to cents
        currency: "brl",
        metadata: {
          userId: req.user.id,
          packageId: packageId, // Store packageId instead of credits
          stripeMode: user.stripeMode || 'test', // Store mode for webhook processing
        },
      });

      res.json({ clientSecret: paymentIntent.client_secret });
    } catch (error: any) {
      res.status(500).json({ message: "Error creating payment intent: " + error.message });
    }
  });

  // Stripe webhook endpoint (no auth required - Stripe handles verification)
  app.post("/api/stripe-webhook", express.raw({ type: 'application/json' }), async (req, res) => {
    const sig = req.headers['stripe-signature'];
    const endpointSecret = process.env.STRIPE_WEBHOOK_SECRET;

    if (!endpointSecret) {
      console.error('‚ùå Missing STRIPE_WEBHOOK_SECRET environment variable');
      return res.status(400).json({ error: 'Webhook secret not configured' });
    }

    let event: Stripe.Event;

    try {
      // Try to verify webhook with test instance first, then live
      try {
        event = stripeTest.webhooks.constructEvent(req.body, sig as string, endpointSecret);
      } catch (testErr) {
        // If test fails, try live instance
        event = stripeLive.webhooks.constructEvent(req.body, sig as string, endpointSecret);
      }
    } catch (err: any) {
      console.error(`‚ùå Webhook signature verification failed: ${err.message}`);
      return res.status(400).json({ error: `Webhook Error: ${err.message}` });
    }

    console.log(`üéØ Received Stripe webhook: ${event.type}`);

    // Handle the event
    switch (event.type) {
      case 'payment_intent.succeeded':
        const paymentIntent = event.data.object as Stripe.PaymentIntent;
        await handleSuccessfulPayment(paymentIntent);
        break;
      case 'payment_intent.payment_failed':
        console.log(`üí≥ Payment failed: ${event.data.object.id}`);
        break;
      default:
        console.log(`ü§∑ Unhandled event type: ${event.type}`);
    }

    res.json({ received: true });
  });

  app.post("/api/confirm-payment", requireSupabaseAuth, async (req: AuthenticatedRequest, res) => {
    try {
      const { paymentIntentId } = req.body;

      // Get user's stripe mode preference to use correct instance
      const user = await storage.getUser(req.user.id);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      const stripe = getStripeInstance(user.stripeMode || 'test');

      const paymentIntent = await stripe.paymentIntents.retrieve(paymentIntentId);

      if (paymentIntent.status === 'succeeded' && paymentIntent.metadata.userId === req.user.id) {
        const { packageId } = paymentIntent.metadata;

        if (!packageId) {
          return res.status(400).json({ message: "Invalid payment: no package ID found" });
        }

        // Get package details from database (trusted source)
        const creditPackage = await storage.getCreditPackage(packageId);

        if (!creditPackage) {
          return res.status(400).json({ message: "Invalid payment: package not found" });
        }

        // Validate payment amount matches package price
        const expectedAmount = Math.round(parseFloat(creditPackage.price) * 100);
        if (paymentIntent.amount !== expectedAmount) {
          return res.status(400).json({ message: "Invalid payment: amount mismatch" });
        }

        const credits = creditPackage.credits;
        const newCredits = req.user.credits + credits;

        await storage.updateUserCredits(req.user.id, newCredits);
        await storage.createCreditTransaction(
          req.user.id,
          "purchase",
          credits,
          `Purchase of ${credits} credits (${creditPackage.name})`,
          paymentIntentId
        );

        // Credits updated in database - no session to update

        res.json({ success: true, newCredits });
      } else {
        res.status(400).json({ message: "Payment not completed" });
      }
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

