      const providers = await storage.getSystemAiProviders();
      const provider = providers.find(p => p.id === id);

      if (!provider || !provider.isActive) {
        return res.status(404).json({ message: "Provedor n√£o encontrado ou inativo" });
      }

      // Test the API key connectivity
      let testResult = { success: false, message: "", provider: provider.provider };

      try {
        switch (provider.provider) {
          case 'openai':
            // Test OpenAI API key by making a simple request
            const openaiTest = await fetch('https://api.openai.com/v1/models', {
              headers: {
                'Authorization': `Bearer ${provider.apiKey}`,
                'Content-Type': 'application/json'
              }
            });
            testResult.success = openaiTest.ok;
            testResult.message = openaiTest.ok ? "Conex√£o com OpenAI estabelecida com sucesso" : `Erro OpenAI: ${openaiTest.statusText}`;
            break;

          case 'anthropic':
            // Test Anthropic API key
            const anthropicTest = await fetch('https://api.anthropic.com/v1/messages', {
              method: 'POST',
              headers: {
                'x-api-key': provider.apiKey,
                'Content-Type': 'application/json',
                'anthropic-version': '2023-06-01'
              },
              body: JSON.stringify({
                model: "claude-3-haiku-20240307",
                max_tokens: 1,
                messages: [{ role: "user", content: "test" }]
              })
            });
            testResult.success = anthropicTest.ok;
            testResult.message = anthropicTest.ok ? "Conex√£o com Anthropic estabelecida com sucesso" : `Erro Anthropic: ${anthropicTest.statusText}`;
            break;

          case 'gemini':
            // Test Gemini API key
            const geminiTest = await fetch(`https://generativelanguage.googleapis.com/v1beta/models?key=${provider.apiKey}`);
            testResult.success = geminiTest.ok;
            testResult.message = geminiTest.ok ? "Conex√£o com Gemini estabelecida com sucesso" : `Erro Gemini: ${geminiTest.statusText}`;
            break;

          default:
            testResult.message = "Provedor n√£o suportado para teste";
        }
      } catch (networkError: any) {
        testResult.success = false;
        testResult.message = `Erro de conectividade: ${networkError.message}`;
      }

      res.json(testResult);
    } catch (error: any) {
      res.status(500).json({ message: error.message });
    }
  });

  // Admin endpoints for batch management
  app.get("/api/admin/batch/jobs", requireSupabaseAdmin, async (req: AuthenticatedRequest, res) => {
