    } catch (error: any) {
      console.error('SMTP test error:', error);

      if (error.name === 'ZodError') {
        return res.status(400).json({ 
          message: 'Dados inv√°lidos', 
          errors: error.errors 
        });
      }

      // Handle email service errors
      if (error.message?.includes('SMTP') || error.message?.includes('configura√ß√£o')) {
        return res.status(503).json({ 
          message: 'Configura√ß√£o SMTP inv√°lida: ' + error.message 
        });
      }

      res.status(500).json({ 
        message: 'Erro ao testar configura√ß√£o SMTP: ' + error.message 
