  async analyzeWithOpenAI(content: string, analysisType: string, apiKey?: string, templateData?: any): Promise<AnalysisResult> {
    const finalApiKey = await this.getApiKeyWithFallback('openai', apiKey);
    if (!finalApiKey) {
