            {isLoading ? "Salvando..." : "Salvar Notifica√ß√£o"}
          </Button>
          <Button type="button" variant="outline" onClick={onCancel} data-testid="button-cancel-notification">
            Cancelar
          </Button>
        </div>
      </form>
    </Form>
  );
}

