import { useState, useEffect } from 'react';
import { useQuery } from '@tanstack/react-query';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { FileText, Users, Building, Shield, Scale, ScrollText } from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';

interface DocumentTemplate {
  id: string;
  name: string;
  description: string;
  category: string;
  documentType: string;
  keyPoints: string[];
  isActive: boolean;
}

interface TemplateSelectorProps {
  selectedTemplate: string;
  onTemplateChange: (templateId: string) => void;
  className?: string;
}

const getCategoryIcon = (category: string) => {
  switch (category) {
    case 'contratos':
      return <FileText size={16} />;
    case 'trabalhista':
      return <Users size={16} />;
    case 'empresarial':
      return <Building size={16} />;
    case 'civil':
      return <Scale size={16} />;
    case 'criminal':
      return <Shield size={16} />;
    case 'constitucional':
      return <ScrollText size={16} />;
    default:
      return <FileText size={16} />;
  }
};

const getCategoryColor = (category: string) => {
  switch (category) {
    case 'contratos':
      return 'bg-blue-100 text-blue-800';
    case 'trabalhista':
      return 'bg-green-100 text-green-800';
    case 'empresarial':
      return 'bg-purple-100 text-purple-800';
    case 'civil':
      return 'bg-orange-100 text-orange-800';
    case 'criminal':
      return 'bg-red-100 text-red-800';
    case 'constitucional':
      return 'bg-yellow-100 text-yellow-800';
    default:
      return 'bg-gray-100 text-gray-800';
  }
};

export function TemplateSelector({ selectedTemplate, onTemplateChange, className = '' }: TemplateSelectorProps) {
  const [selectedTemplateData, setSelectedTemplateData] = useState<DocumentTemplate | null>(null);

  const { data: templates = [], isLoading } = useQuery<DocumentTemplate[]>({
    queryKey: ['/api/templates'],
    queryFn: async () => {
      const response = await apiRequest('GET', '/api/templates');
      return response.json();
    }
  });

  // Update selected template data when selection changes
  useEffect(() => {
    if (selectedTemplate && selectedTemplate !== "no-template" && templates.length > 0) {
      const template = templates.find(t => t.id === selectedTemplate);
      setSelectedTemplateData(template || null);
    } else {
      setSelectedTemplateData(null);
    }
  }, [selectedTemplate, templates]);

  // Group templates by category
  const templatesByCategory = templates.reduce((acc, template) => {
    if (!acc[template.category]) {
      acc[template.category] = [];
    }
    acc[template.category].push(template);
    return acc;
  }, {} as Record<string, DocumentTemplate[]>);

  if (isLoading) {
    return (
      <div className={`space-y-2 ${className}`}>
        <Label>Tipo de Documento (Carregando...)</Label>
        <div className="h-10 bg-muted animate-pulse rounded-md" />
      </div>
    );
  }

  return (
    <div className={`space-y-4 ${className}`}>
      <div className="space-y-2">
        <Label htmlFor="template-select" data-testid="label-template-selector">
          Tipo de Documento
        </Label>
        <Select value={selectedTemplate} onValueChange={onTemplateChange}>
          <SelectTrigger id="template-select" data-testid="select-template">
            <SelectValue placeholder="Selecione um tipo de documento para an√°lise especializada" />
          </SelectTrigger>
          <SelectContent>
            <SelectItem value="no-template" data-testid="option-no-template">
              An√°lise Geral (sem template espec√≠fico)
            </SelectItem>
            {Object.entries(templatesByCategory).map(([category, categoryTemplates]) => (
              <div key={category}>
                <div className="flex items-center gap-2 px-2 py-1.5 text-sm font-medium text-muted-foreground">
                  {getCategoryIcon(category)}
                  <span className="capitalize">{category}</span>
                </div>
                {categoryTemplates.map((template) => (
                  <SelectItem 
                    key={template.id} 
                    value={template.id}
                    data-testid={`option-template-${template.id}`}
                  >
                    <div className="flex items-center gap-2">
                      <span>{template.name}</span>
                      <Badge variant="secondary" className={getCategoryColor(template.category)}>
                        {template.documentType}
                      </Badge>
                    </div>
                  </SelectItem>
                ))}
              </div>
            ))}
          </SelectContent>
        </Select>
      </div>

      {/* Template Details Card */}
      {selectedTemplateData && (
        <Card data-testid="card-template-details">
          <CardHeader className="pb-3">
            <div className="flex items-start justify-between">
              <div>
                <CardTitle className="text-base">{selectedTemplateData.name}</CardTitle>
                <CardDescription className="mt-1">
                  {selectedTemplateData.description}
                </CardDescription>
              </div>
              <Badge className={getCategoryColor(selectedTemplateData.category)}>
                {selectedTemplateData.category}
              </Badge>
            </div>
          </CardHeader>
          <CardContent className="pt-0">
            <div>
              <h4 className="text-sm font-medium mb-2">Pontos de An√°lise Espec√≠ficos:</h4>
              <ul className="text-sm text-muted-foreground space-y-1">
                {selectedTemplateData.keyPoints.map((point, index) => (
                  <li key={index} className="flex items-start gap-2">
                    <span className="w-1.5 h-1.5 bg-primary rounded-full mt-2 flex-shrink-0" />
                    <span>{point}</span>
                  </li>
                ))}
              </ul>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/pages/batch.tsx.content:
import { useState, useCallback, useEffect } from "react";
import { useDropzone } from "react-dropzone";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Progress } from "@/components/ui/progress";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import type { BatchJob, BatchStatistics, Template } from "@shared/schema";
import { Upload, FileText, X, Play, Calculator, AlertCircle, CheckCircle2, Clock, FileWarning } from "lucide-react";

interface BatchUploadFile {
  file: File;
  id: string;
  status: 'pending' | 'uploading' | 'completed' | 'error';
  error?: string;
}


export default function BatchProcessingPage() {
  const [selectedFiles, setSelectedFiles] = useState<BatchUploadFile[]>([]);
  const [batchName, setBatchName] = useState("");
  const [batchDescription, setBatchDescription] = useState("");
  const [analysisType, setAnalysisType] = useState("");
  const [aiProvider, setAiProvider] = useState("openai");
  const [aiModel, setAiModel] = useState("gpt-4");
  const [templateId, setTemplateId] = useState("");
  const [estimatedCredits, setEstimatedCredits] = useState(0);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch user's batch jobs
  const { data: batchJobs = [], isLoading: loadingJobs } = useQuery<BatchJob[]>({
    queryKey: ['/api/batch/jobs'],
    enabled: true
  });

  // Fetch templates for dropdown
  const { data: templates = [] } = useQuery<Template[]>({
    queryKey: ['/api/templates']
  });

  // Fetch batch statistics
  const { data: statistics } = useQuery<BatchStatistics>({
    queryKey: ['/api/batch/statistics']
  });

  // Calculate credits when files or settings change
  const calculateCredits = useCallback(() => {
    if (selectedFiles.length === 0 || !aiProvider || !aiModel) {
      setEstimatedCredits(0);
      return;
    }

    // Credit calculation based on provider and model
    const creditsPerDocument = getProviderCredits(`${aiProvider}-${aiModel}`);
    setEstimatedCredits(creditsPerDocument * selectedFiles.length);
  }, [selectedFiles.length, aiProvider, aiModel]);

  // Helper function to get credits per document (matches backend logic)
  const getProviderCredits = (providerModel: string): number => {
    const creditMap: Record<string, number> = {
      'openai-gpt-4': 5,
      'openai-gpt-3.5-turbo': 2,
      'anthropic-claude-3-opus': 6,
      'anthropic-claude-3-sonnet': 4,
      'gemini-gemini-pro': 3,
      'free-basic': 1
    };
    return creditMap[providerModel] || 1;
  };

  // Recalculate credits when dependencies change
  useEffect(() => {
    calculateCredits();
  }, [calculateCredits]);

  // Dropzone configuration
  const onDrop = useCallback((acceptedFiles: File[]) => {
    const newFiles: BatchUploadFile[] = acceptedFiles.map(file => ({
      file,
      id: Math.random().toString(36).substr(2, 9),
      status: 'pending' as const
    }));

    setSelectedFiles(prev => [...prev, ...newFiles]);
    calculateCredits();

    toast({
      title: "Files added",
      description: `Added ${acceptedFiles.length} files to batch`
    });
  }, [calculateCredits, toast]);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxSize: 50 * 1024 * 1024, // 50MB per file
    maxFiles: 25, // UPDATED: Match backend limit (was 50)
    multiple: true
  });

  // Remove file from batch
  const removeFile = (fileId: string) => {
    setSelectedFiles(prev => prev.filter(f => f.id !== fileId));
    calculateCredits();
  };

  // Create batch job mutation
  const createBatchMutation = useMutation({
    mutationFn: async (data: FormData) => {
      return await apiRequest('POST', '/api/batch/create', data);
    },
    onSuccess: (result) => {
      toast({
        title: "Batch job created",
        description: `Batch "${batchName}" created successfully with ${selectedFiles.length} documents`
      });

      // Reset form
      setSelectedFiles([]);
      setBatchName("");
      setBatchDescription("");
      setAnalysisType("");
      setTemplateId("");

      // Refresh batch jobs list
      queryClient.invalidateQueries({ queryKey: ['/api/batch/jobs'] });
      queryClient.invalidateQueries({ queryKey: ['/api/batch/statistics'] });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Failed to create batch job",
        description: error.message || "An error occurred while creating the batch job"
      });
    }
  });

  // Submit batch job
  const handleSubmit = async () => {
    if (!batchName.trim()) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please enter a batch name"
      });
      return;
    }

    if (selectedFiles.length === 0) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please select at least one file"
      });
      return;
    }

    if (!analysisType) {
      toast({
        variant: "destructive",
        title: "Validation Error",
        description: "Please select an analysis type"
      });
      return;
    }

    const formData = new FormData();
    formData.append('name', batchName);
    formData.append('description', batchDescription);
    formData.append('analysisType', analysisType);
    formData.append('aiProvider', aiProvider);
    formData.append('aiModel', aiModel);
    if (templateId && templateId !== "no-template") {
      formData.append('templateId', templateId);
    }

    selectedFiles.forEach(({ file }) => {
      formData.append('files', file);
    });

    createBatchMutation.mutate(formData);
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle2 className="h-4 w-4 text-green-500" />;
      case 'failed':
        return <AlertCircle className="h-4 w-4 text-red-500" />;
      case 'processing':
        return <Clock className="h-4 w-4 text-blue-500" />;
      case 'completed_with_errors':
        return <FileWarning className="h-4 w-4 text-yellow-500" />;
      default:
        return <Clock className="h-4 w-4 text-gray-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      case 'processing':
        return 'bg-blue-100 text-blue-800';
      case 'completed_with_errors':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="container mx-auto py-8 space-y-8">
      <div>
        <h1 className="text-3xl font-bold" data-testid="text-page-title">Batch Document Processing</h1>
        <p className="text-muted-foreground mt-2">
          Process multiple documents simultaneously with AI analysis
        </p>
      </div>

      {/* Statistics Cards */}
      {statistics && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardHeader className="pb-2">
              <CardDescription>Total Batches</CardDescription>
              <CardTitle className="text-2xl" data-testid="text-total-batches">{statistics.totalBatches}</CardTitle>
            </CardHeader>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardDescription>Completed</CardDescription>
              <CardTitle className="text-2xl text-green-600" data-testid="text-completed-batches">{statistics.completedBatches}</CardTitle>
            </CardHeader>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardDescription>Documents Processed</CardDescription>
              <CardTitle className="text-2xl" data-testid="text-documents-processed">{statistics.totalDocumentsProcessed}</CardTitle>
            </CardHeader>
          </Card>
          <Card>
            <CardHeader className="pb-2">
              <CardDescription>Avg Processing Time</CardDescription>
              <CardTitle className="text-2xl" data-testid="text-avg-processing-time">
                {statistics.averageProcessingTime ? `${Math.round(statistics.averageProcessingTime)}s` : '0s'}
              </CardTitle>
            </CardHeader>
          </Card>
        </div>
      )}

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Upload Section */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Upload className="h-5 w-5" />
              Create New Batch
            </CardTitle>
            <CardDescription>
              Upload multiple documents for batch processing
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Batch Details */}
            <div className="space-y-4">
              <div>
                <Label htmlFor="batch-name">Batch Name *</Label>
                <Input
                  id="batch-name"
                  data-testid="input-batch-name"
                  value={batchName}
                  onChange={(e) => setBatchName(e.target.value)}
                  placeholder="Enter batch name"
                />
              </div>

              <div>
                <Label htmlFor="batch-description">Description</Label>
                <Textarea
                  id="batch-description"
                  data-testid="input-batch-description"
                  value={batchDescription}
                  onChange={(e) => setBatchDescription(e.target.value)}
                  placeholder="Optional batch description"
                  rows={3}
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="analysis-type">Analysis Type *</Label>
                  <Select value={analysisType} onValueChange={setAnalysisType}>
                    <SelectTrigger data-testid="select-analysis-type">
                      <SelectValue placeholder="Select analysis type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="risk_assessment">Risk Assessment</SelectItem>
                      <SelectItem value="compliance_check">Compliance Check</SelectItem>
                      <SelectItem value="contract_review">Contract Review</SelectItem>
                      <SelectItem value="legal_summary">Legal Summary</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="template">Template (Optional)</Label>
                  <Select value={templateId} onValueChange={setTemplateId}>
                    <SelectTrigger data-testid="select-template">
                      <SelectValue placeholder="Select template" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="no-template">No template</SelectItem>
                      {templates.map((template) => (
                        <SelectItem key={template.id} value={template.id}>
                          {template.name}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label htmlFor="ai-provider">AI Provider</Label>
                  <Select value={aiProvider} onValueChange={setAiProvider}>
                    <SelectTrigger data-testid="select-ai-provider">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="openai">OpenAI</SelectItem>
                      <SelectItem value="anthropic">Anthropic</SelectItem>
                      <SelectItem value="gemini">Google Gemini</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                <div>
                  <Label htmlFor="ai-model">AI Model</Label>
                  <Select value={aiModel} onValueChange={setAiModel}>
                    <SelectTrigger data-testid="select-ai-model">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      {aiProvider === 'openai' && (
                        <>
                          <SelectItem value="gpt-4">GPT-4</SelectItem>
                          <SelectItem value="gpt-3.5-turbo">GPT-3.5 Turbo</SelectItem>
                        </>
                      )}
                      {aiProvider === 'anthropic' && (
                        <>
                          <SelectItem value="claude-3-opus">Claude 3 Opus</SelectItem>
                          <SelectItem value="claude-3-sonnet">Claude 3 Sonnet</SelectItem>
                        </>
                      )}
                      {aiProvider === 'gemini' && (
                        <SelectItem value="gemini-pro">Gemini Pro</SelectItem>
                      )}
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </div>

            <Separator />

            {/* File Upload */}
            <div
              {...getRootProps()}
              className={`border-2 border-dashed rounded-lg p-8 text-center cursor-pointer transition-colors
                ${isDragActive ? 'border-primary bg-primary/5' : 'border-muted-foreground/25 hover:border-primary/50'}`}
              data-testid="dropzone-upload"
            >
              <input {...getInputProps()} />
              <Upload className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
              <h3 className="text-lg font-medium mb-2">
                {isDragActive ? 'Drop files here' : 'Upload Documents'}
              </h3>
              <p className="text-muted-foreground mb-4">
                Drag & drop files here, or click to select files
              </p>
              <p className="text-sm text-muted-foreground">
                Supports PDF, DOC, DOCX, TXT files up to 50MB each
              </p>
            </div>

            {/* Selected Files */}
            {selectedFiles.length > 0 && (
              <div>
                <h4 className="font-medium mb-3">Selected Files ({selectedFiles.length})</h4>
                <div className="space-y-2 max-h-40 overflow-y-auto">
                  {selectedFiles.map(({ file, id, status }) => (
                    <div
                      key={id}
                      className="flex items-center justify-between p-3 bg-muted rounded-lg"
                      data-testid={`file-item-${id}`}
                    >
                      <div className="flex items-center gap-3">
                        <FileText className="h-4 w-4 text-muted-foreground" />
                        <div>
                          <p className="text-sm font-medium">{file.name}</p>
                          <p className="text-xs text-muted-foreground">
                            {(file.size / 1024 / 1024).toFixed(2)} MB
                          </p>
                        </div>
                      </div>
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => removeFile(id)}
                        data-testid={`button-remove-file-${id}`}
                      >
                        <X className="h-4 w-4" />
                      </Button>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Credit Estimation */}
            {selectedFiles.length > 0 && (
              <Alert>
                <Calculator className="h-4 w-4" />
                <AlertDescription>
                  <div className="flex items-center justify-between">
                    <span>Estimated Credits: <strong>{estimatedCredits}</strong></span>
                    <span className="text-sm text-muted-foreground">
                      {getProviderCredits(`${aiProvider}-${aiModel}`)} credits √ó {selectedFiles.length} files
                    </span>
                  </div>
                </AlertDescription>
              </Alert>
            )}

            {/* Submit Button */}
            <Button
              onClick={handleSubmit}
              disabled={selectedFiles.length === 0 || !batchName.trim() || !analysisType || createBatchMutation.isPending}
              className="w-full"
              data-testid="button-create-batch"
            >
              <Play className="h-4 w-4 mr-2" />
              {createBatchMutation.isPending ? 'Creating Batch...' : `Create Batch (${estimatedCredits} credits)`}
            </Button>
          </CardContent>
        </Card>

        {/* Recent Batches */}
        <Card>
          <CardHeader>
            <CardTitle>Recent Batch Jobs</CardTitle>
            <CardDescription>
              Your recent batch processing jobs
            </CardDescription>
          </CardHeader>
          <CardContent>
            {loadingJobs ? (
              <div className="space-y-3">
                {[...Array(3)].map((_, i) => (
                  <div key={i} className="h-16 bg-muted rounded-lg animate-pulse" />
                ))}
              </div>
            ) : batchJobs.length === 0 ? (
              <div className="text-center py-8 text-muted-foreground">
                <FileText className="h-12 w-12 mx-auto mb-4 opacity-50" />
                <p>No batch jobs yet</p>
                <p className="text-sm">Create your first batch to get started</p>
              </div>
            ) : (
              <div className="space-y-3">
                {batchJobs.slice(0, 5).map((job) => (
                  <div
                    key={job.id}
                    className="p-4 border rounded-lg space-y-2"
                    data-testid={`batch-job-${job.id}`}
                  >
                    <div className="flex items-center justify-between">
                      <h4 className="font-medium">{job.name}</h4>
                      <Badge className={getStatusColor(job.status)}>
                        {getStatusIcon(job.status)}
                        <span className="ml-1 capitalize">{job.status.replace('_', ' ')}</span>
                      </Badge>
                    </div>

                    <div className="flex items-center justify-between text-sm text-muted-foreground">
                      <span>{job.totalDocuments} documents</span>
                      <span>{job.totalCreditsUsed || job.totalCreditsEstimated} credits</span>
                    </div>

                    {job.status === 'processing' && (
                      <Progress 
                        value={(job.processedDocuments / job.totalDocuments) * 100} 
                        className="h-2"
                      />
                    )}

                    <div className="flex items-center justify-between text-xs text-muted-foreground">
                      <span>Created {new Date(job.createdAt).toLocaleDateString()}</span>
                      <span>{job.processedDocuments}/{job.totalDocuments} processed</span>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

server/services/batchProcessor.ts.content:
import { storage } from "../storage";
import { aiService } from "./ai";
import type { QueueJob, BatchJob, BatchDocument, BatchJobMetadata, BatchDocumentMetadata } from "@shared/schema";
import fs from "fs";

interface BatchProcessingJob {
  batchJobId: string;
  userId: string;
  aiProvider: string;
  aiModel: string;
  analysisType: string;
  templateId?: string;
}

class BatchProcessor {
  private isProcessing = false;
  private processingInterval: NodeJS.Timeout | null = null;
  private maxConcurrentJobs = 5; // Process up to 5 documents in parallel
  private pollingInterval = 5000; // Check for new jobs every 5 seconds

  constructor() {
    this.startPolling();
  }

  private startPolling() {
    if (this.processingInterval) return;

    console.log("üîÑ Starting batch processor polling...");
    this.processingInterval = setInterval(async () => {
      if (!this.isProcessing) {
        await this.processNextJob();
      }
    }, this.pollingInterval);
  }

  public stopPolling() {
    if (this.processingInterval) {
      clearInterval(this.processingInterval);
      this.processingInterval = null;
      console.log("‚èπÔ∏è Stopped batch processor polling");
    }
  }

  private async processNextJob() {
    try {
      const queueJob = await storage.getNextQueueJob();
      if (!queueJob) return; // No jobs to process

      console.log(`üìã Processing queue job: ${queueJob.id}`);
      await this.processQueueJob(queueJob);
    } catch (error) {
      console.error("‚ùå Error in batch processor:", error);
    }
  }

  private async processQueueJob(queueJob: QueueJob) {
    if (queueJob.jobType !== 'batch_processing') {
      console.log(`‚è≠Ô∏è Skipping non-batch job: ${queueJob.jobType}`);
      return;
    }

    this.isProcessing = true;

    try {
      // Update queue job status to processing
      await storage.updateQueueJobStatus(queueJob.id, 'processing');

      const jobData = queueJob.jobData as BatchProcessingJob;
      await this.processBatchJob(jobData);

      // Mark queue job as completed
      await storage.updateQueueJobStatus(queueJob.id, 'completed');
      console.log(`‚úÖ Completed queue job: ${queueJob.id}`);
    } catch (error: any) {
      console.error(`‚ùå Failed to process queue job ${queueJob.id}:`, error);

      // Update queue job with error
      await storage.updateQueueJobStatus(queueJob.id, 'failed', error.message);

      // Update batch job status to failed
      const jobData = queueJob.jobData as BatchProcessingJob;
      if (jobData.batchJobId) {
        await storage.updateBatchJobStatus(jobData.batchJobId, 'failed', error.message);
      }
    } finally {
      this.isProcessing = false;
    }
  }

  private async processBatchJob(jobData: BatchProcessingJob) {
    const { batchJobId, userId, aiProvider, aiModel, analysisType, templateId } = jobData;

    console.log(`üöÄ Starting batch processing for job: ${batchJobId}`);

    // Get batch job and documents
    const batchJob = await storage.getBatchJobById(batchJobId);
    if (!batchJob) {
      throw new Error(`Batch job not found: ${batchJobId}`);
    }

    const batchDocuments = await storage.getBatchDocuments(batchJobId);
    if (batchDocuments.length === 0) {
      throw new Error(`No documents found for batch job: ${batchJobId}`);
    }

    // Update batch job status to processing
    await storage.updateBatchJobStatus(batchJobId, 'processing');

    // Get user to check credits and API keys
    const user = await storage.getUser(userId);
    if (!user) {
      throw new Error(`User not found: ${userId}`);
    }

    // Get user's API key for the provider if needed
    let userApiKey: string | undefined;
    if (aiProvider !== 'free') {
      const providerConfig = await storage.getAiProvider(userId, aiProvider);
      userApiKey = providerConfig?.apiKey;
    }

    // Get template data if specified
    let templateData = null;
    if (templateId) {
      templateData = await storage.getTemplateWithPrompts(templateId);
    }

    let totalCreditsUsed = 0;
    let processedCount = 0;
    let failedCount = 0;

    try {
      // Process documents in parallel batches
      const chunkSize = this.maxConcurrentJobs;
      const documentChunks = this.chunkArray(batchDocuments, chunkSize);

      for (const chunk of documentChunks) {
        const promises = chunk.map(doc => this.processDocument(
          doc,
          userId,
          aiProvider,
          aiModel,
          analysisType,
          userApiKey,
          templateId
        ));

        const results = await Promise.allSettled(promises);

        // Process results
        for (let i = 0; i < results.length; i++) {
          const result = results[i];
          const document = chunk[i];

          if (result.status === 'fulfilled') {
            const { creditsUsed } = result.value;
            totalCreditsUsed += creditsUsed;
            processedCount++;
            console.log(`‚úÖ Processed document: ${document.originalFileName}`);
          } else {
            failedCount++;
            console.error(`‚ùå Failed to process document ${document.originalFileName}:`, result.reason);
            await storage.updateBatchDocumentStatus(document.id, 'failed', result.reason?.message || 'Processing failed');
          }
        }

        // Update batch job progress
        const progressPercentage = Math.round(((processedCount + failedCount) / batchDocuments.length) * 100);
        await storage.updateBatchJob(batchJobId, {
          processedDocuments: processedCount,
          failedDocuments: failedCount,
          totalCreditsUsed,
          metadata: { 
            ...(batchJob.metadata as BatchJobMetadata || {}), 
            progressPercentage 
          } as BatchJobMetadata
        });
      }

      // Determine final status
      const finalStatus = failedCount === batchDocuments.length ? 'failed' : 
                         failedCount > 0 ? 'completed_with_errors' : 'completed';

      // Update final batch job status
      await storage.updateBatchJobStatus(batchJobId, finalStatus);

      console.log(`üéâ Batch processing completed for ${batchJobId}: ${processedCount} success, ${failedCount} failed`);

    } catch (error: any) {
      console.error(`‚ùå Batch processing failed for ${batchJobId}:`, error);
      await storage.updateBatchJobStatus(batchJobId, 'failed', error.message);
      throw error;
    }
  }

  private async processDocument(
    document: BatchDocument,
    userId: string,
    aiProvider: string,
    aiModel: string,
    analysisType: string,
    userApiKey?: string,
    templateId?: string
  ): Promise<{ creditsUsed: number }> {
    // UPDATED: Extract file metadata once for cleanup in finally block
    const metadata = document.metadata as BatchDocumentMetadata & { filePath?: string; tempFile?: boolean };

    try {
      // Update document status to processing
      await storage.updateBatchDocumentStatus(document.id, 'processing');

      const filePath = metadata?.filePath;

      if (!filePath) {
        throw new Error('File path not found in document metadata');
      }

      if (!fs.existsSync(filePath)) {
        throw new Error(`File not found on disk: ${filePath}`);
      }

      // Read file content from disk - SECURE: No more base64 in memory
      let content: string;
      try {
        const fileBuffer = await fs.promises.readFile(filePath);
        content = fileBuffer.toString('utf-8');
        console.log(`üìù Read file from disk: ${document.originalFileName} (${fileBuffer.length} bytes)`);
      } catch (readError: any) {
        throw new Error(`Failed to read file ${filePath}: ${readError.message}`);
      }

      // Calculate credits needed
      const creditsNeeded = aiService.getProviderCredits(`${aiProvider}-${aiModel}`);

      // Create document analysis record
      const analysis = await storage.createDocumentAnalysis(userId, {
        title: `Batch: ${document.originalFileName}`,
        content,
        aiProvider,
        aiModel,
        analysisType,
        templateId: templateId || null,
        result: {},
        creditsUsed: creditsNeeded,
      });

      try {
        // Perform AI analysis
        const result = await aiService.analyzeDocument(
          content,
          analysisType,
          aiProvider,
          aiModel,
          userApiKey,
          templateId
        );

        // Update analysis with result
        await storage.updateDocumentAnalysisResult(analysis.id, result, "completed");

        // Link batch document to analysis
        await storage.linkBatchDocumentToAnalysis(document.id, analysis.id);

        // Update document status to completed
        await storage.updateBatchDocumentStatus(document.id, 'completed');

        // FIXED: Credits were already deducted at batch creation, so don't deduct again
        return { creditsUsed: creditsNeeded };

      } catch (analysisError: any) {
        // Update analysis as failed
        await storage.updateDocumentAnalysisResult(analysis.id, { error: analysisError.message }, "failed");
        throw analysisError;
      }

    } catch (error: any) {
      console.error(`‚ùå Error processing document ${document.originalFileName}:`, error);
      await storage.updateBatchDocumentStatus(document.id, 'failed', error.message);
      throw error;
    } finally {
      // REQUIRED: Always clean up temp file in finally block regardless of success/failure
      try {
        await this.cleanupDocumentFile(document, metadata);
      } catch (cleanupError) {
        console.error(`‚ùå Failed to cleanup file in finally block:`, cleanupError);
      }
    }
  }

  private chunkArray<T>(array: T[], chunkSize: number): T[][] {
    const chunks: T[][] = [];
    for (let i = 0; i < array.length; i += chunkSize) {
      chunks.push(array.slice(i, i + chunkSize));
    }
    return chunks;
  }

  // Helper method to clean up temporary files after processing
  private async cleanupDocumentFile(document: BatchDocument, metadata: any) {
    if (metadata?.tempFile && metadata?.filePath) {
      try {
        if (fs.existsSync(metadata.filePath)) {
          await fs.promises.unlink(metadata.filePath);
          console.log(`üóëÔ∏è Cleaned up temp file: ${metadata.filePath}`);
        }
      } catch (error) {
        console.error(`‚ùå Failed to cleanup temp file ${metadata.filePath}:`, error);
      }
    }
  }

  // Public methods for external control
  public async retryBatchJob(batchJobId: string) {
    try {
      const batchJob = await storage.getBatchJobById(batchJobId);
      if (!batchJob) {
        throw new Error(`Batch job not found: ${batchJobId}`);
      }

      // Reset batch job status
      await storage.updateBatchJobStatus(batchJobId, 'pending');

      // Reset failed documents to pending
      const documents = await storage.getBatchDocuments(batchJobId);
      const failedDocuments = documents.filter(doc => doc.status === 'failed');

      for (const doc of failedDocuments) {
        await storage.updateBatchDocumentStatus(doc.id, 'pending');
      }

      // Create new queue job
      await storage.createQueueJob({
        jobType: 'batch_processing',
        jobData: {
          batchJobId: batchJob.id,
          userId: batchJob.userId,
          aiProvider: batchJob.aiProvider,
          aiModel: batchJob.aiModel,
          analysisType: batchJob.analysisType,
          templateId: batchJob.templateId
        },
        priority: 2 // Higher priority for retries
      });

      console.log(`üîÑ Retry initiated for batch job: ${batchJobId}`);
    } catch (error) {
      console.error(`‚ùå Error retrying batch job ${batchJobId}:`, error);
      throw error;
    }
  }

  public getStatus() {
    return {
      isProcessing: this.isProcessing,
      pollingActive: this.processingInterval !== null,
      maxConcurrentJobs: this.maxConcurrentJobs,
      pollingInterval: this.pollingInterval
    };
  }
}

// Create singleton instance
export const batchProcessor = new BatchProcessor();

// Graceful shutdown
process.on('SIGTERM', () => {
  console.log('üõë Shutting down batch processor...');
  batchProcessor.stopPolling();
});

process.on('SIGINT', () => {
  console.log('üõë Shutting down batch processor...');
  batchProcessor.stopPolling();
});

