import { useState, useEffect } from 'react';
import { useRoute, useLocation } from 'wouter';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { ArrowLeft, Clock, Coins, AlertTriangle, CheckCircle, FileText, Download, Share, Eye, EyeOff, Trash2 } from 'lucide-react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { Link } from 'wouter';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';
interface DocumentAnalysis {
  id: string;
  title: string;
  fileName: string;
  fileSize: number;
  content: string;
  analysisType: string;
  aiProvider: string;
  aiModel: string;
  creditsUsed: number;
  result: {
    riskLevel: string;
    summary: string;
    criticalFlaws: string[];
    recommendations: string[];
    legalCompliance: {
      score: number;
      issues: string[];
    };
  };
  createdAt: string;
}

export default function AnalysisDetails() {
  const [match, params] = useRoute("/analyses/:id");
  const [showFullText, setShowFullText] = useState(false);
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const analysisId = params?.id;

  // Scroll to top when page loads or analysis ID changes
  useEffect(() => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  }, [analysisId]);

  const { data: analysis, isLoading, error } = useQuery<DocumentAnalysis>({
    queryKey: ['/api/analyses', analysisId],
    queryFn: async () => {
      const response = await apiRequest('GET', `/api/analyses/${analysisId}`);
      return response.json();
    },
    enabled: !!analysisId
  });

  const deleteAnalysisMutation = useMutation({
    mutationFn: async () => {
      if (!analysisId) throw new Error('No analysis ID');
      const response = await apiRequest('DELETE', `/api/analyses/${analysisId}`);
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "An√°lise Exclu√≠da",
        description: "An√°lise movida para lixeira. Voc√™ pode restaur√°-la nos pr√≥ximos 7 dias.",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/analyses'] });
      setLocation('/dashboard');
    },
    onError: (error: any) => {
      toast({
        title: "Erro ao Excluir",
        description: error.message || "Erro inesperado ao excluir an√°lise",
        variant: "destructive",
      });
    }
  });

  const getRiskBadgeColor = (risk: string) => {
    switch (risk.toLowerCase()) {
      case 'low':
      case 'baixo':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      case 'medium':
      case 'm√©dio':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-300';
      case 'high':
      case 'alto':
        return 'bg-orange-100 text-orange-800 dark:bg-orange-900 dark:text-orange-300';
      case 'critical':
      case 'cr√≠tico':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
    }
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background">
        <div className="container mx-auto px-4 py-8 max-w-4xl">
          <div className="animate-pulse">
            <div className="h-8 bg-muted rounded w-1/4 mb-6"></div>
            <div className="space-y-4">
              <div className="h-32 bg-muted rounded"></div>
              <div className="h-24 bg-muted rounded"></div>
              <div className="h-40 bg-muted rounded"></div>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (error || !analysis) {
    return (
      <div className="min-h-screen bg-background">
        <div className="container mx-auto px-4 py-8 max-w-4xl">
          <Link href="/analyses">
            <Button variant="ghost" className="mb-6" data-testid="button-back-analyses">
              <ArrowLeft size={16} className="mr-2" />
              Voltar para An√°lises
            </Button>
          </Link>
          <Card>
            <CardContent className="p-8 text-center">
              <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
              <h2 className="text-xl font-semibold mb-2">An√°lise n√£o encontrada</h2>
              <p className="text-muted-foreground">Esta an√°lise n√£o existe ou voc√™ n√£o tem permiss√£o para acess√°-la.</p>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-background">
      <div className="container mx-auto px-4 py-8 max-w-4xl">
        {/* Header */}
        <div className="flex items-center justify-between mb-6">
          <Link href="/analyses">
            <Button variant="ghost" data-testid="button-back-analyses">
              <ArrowLeft size={16} className="mr-2" />
              Voltar para An√°lises
            </Button>
          </Link>

          <div className="flex gap-2">
            <Button variant="outline" size="sm" data-testid="button-share-analysis">
              <Share size={14} className="mr-2" />
              Compartilhar
            </Button>
            <Button variant="outline" size="sm" data-testid="button-download-analysis">
              <Download size={14} className="mr-2" />
              Baixar PDF
            </Button>
            <Dialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
              <DialogTrigger asChild>
                <Button 
                  variant="outline" 
                  size="sm" 
                  className="text-red-600 hover:text-red-700 hover:bg-red-50 dark:text-red-400 dark:hover:bg-red-950" 
                  data-testid="button-delete-analysis"
                >
                  <Trash2 size={14} className="mr-2" />
                  Excluir
                </Button>
              </DialogTrigger>
              <DialogContent>
                <DialogHeader>
                  <DialogTitle>Confirmar Exclus√£o</DialogTitle>
                  <DialogDescription>
                    Tem certeza que deseja excluir esta an√°lise? Ela ser√° movida para a lixeira e 
                    poder√° ser restaurada nos pr√≥ximos 7 dias. Ap√≥s esse per√≠odo, ser√° exclu√≠da permanentemente.
                  </DialogDescription>
                </DialogHeader>
                <div className="flex justify-end gap-2 mt-4">
                  <Button 
                    variant="outline" 
                    onClick={() => setShowDeleteDialog(false)}
                    data-testid="button-cancel-delete"
                  >
                    Cancelar
                  </Button>
                  <Button 
                    variant="destructive" 
                    onClick={() => deleteAnalysisMutation.mutate()}
                    disabled={deleteAnalysisMutation.isPending}
                    data-testid="button-confirm-delete"
                  >
                    {deleteAnalysisMutation.isPending ? 'Excluindo...' : 'Excluir An√°lise'}
                  </Button>
                </div>
              </DialogContent>
            </Dialog>
          </div>
        </div>

        {/* Analysis Header Card */}
        <Card className="mb-6" data-testid="card-analysis-header">
          <CardHeader>
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <CardTitle className="text-2xl mb-2" data-testid="text-analysis-title">
                  {analysis.title}
                </CardTitle>
                <CardDescription className="text-base">
                  <div className="flex items-center gap-4 text-sm">
                    <div className="flex items-center gap-2">
                      <Clock size={14} />
                      <span>{new Date(analysis.createdAt).toLocaleDateString('pt-BR', {
                        year: 'numeric',
                        month: 'long',
                        day: 'numeric',
                        hour: '2-digit',
                        minute: '2-digit'
                      })}</span>
                    </div>
                    <div className="flex items-center gap-2">
                      <span className="font-medium">Provedor:</span>
                      <span>{analysis.aiProvider} - {analysis.aiModel}</span>
                    </div>
                    <div className="flex items-center gap-1">
                      <Coins className="h-4 w-4 text-primary" />
                      <span>{analysis.creditsUsed} cr√©ditos</span>
                    </div>
                  </div>
                </CardDescription>
              </div>
              <Badge 
                className={getRiskBadgeColor(analysis.result.riskLevel)}
                data-testid="badge-risk-level"
              >
                Risco: {analysis.result.riskLevel}
              </Badge>
            </div>
          </CardHeader>
        </Card>

        {/* Original Document */}
        <Card className="mb-6" data-testid="card-original-document">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <FileText size={20} />
              Documento Original
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div className="flex items-center justify-between">
                <div className="text-sm text-muted-foreground">
                  <strong>Fonte:</strong> {analysis.fileName || 'Texto colado'} 
                  {analysis.fileSize && analysis.fileSize > 0 && (
                    <>
                      {' ‚Ä¢ '}
                      <strong>Tamanho:</strong> {Math.round(analysis.fileSize / 1024)} KB
                    </>
                  )}
                </div>
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => setShowFullText(!showFullText)}
                  data-testid="button-toggle-document-text"
                >
                  {showFullText ? <EyeOff size={14} /> : <Eye size={14} />}
                  <span className="ml-2">{showFullText ? 'Ocultar' : 'Ver'} Texto</span>
                </Button>
              </div>

              {showFullText && (
                <div className="bg-muted/50 p-4 rounded-lg border">
                  <p className="text-sm font-mono whitespace-pre-wrap" data-testid="text-document-content">
                    {analysis.content}
                  </p>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Summary */}
          <Card data-testid="card-summary">
            <CardHeader>
              <CardTitle>Resumo Executivo</CardTitle>
            </CardHeader>
            <CardContent>
              <p className="text-sm leading-relaxed" data-testid="text-summary">
                {analysis.result.summary}
              </p>
            </CardContent>
          </Card>

          {/* Legal Compliance */}
          <Card data-testid="card-compliance">
